import React, { useContext, useState } from 'react';
import './Login.css';
import { AdminContext } from '../../context/AdminContext';

const Login = () => {
  const[state, setState] = useState('Admin');
  const{setAToken, backendUrl} = useContext(AdminContext);
  const[email, setEmail] = useState('');
  const[password, setPassword] = useState('');

  return (
    <div className='login'>
      <p className='title text-center fw-bold'><span> {state} </span> Login </p>
      <form>
        <div className='mb-3'>
          <p className='mb-1'>Email</p>
          <input type='email' className='w-100 px-2 py-1' name='email' value={email} onChange={(e)=>setEmail(e.target.value)}  required/>
        </div>
        <div className='mb-3'>
          <p className='mb-1'>Password</p>
          <input type='password' className='w-100 px-2 py-1' name='password' value={password} onChange={(e)=>setPassword(e.target.value)}  required/>
        </div>
        <button type='submit' className='mb-3 w-100 px-2 py-1'>Login</button>
        {state === 'Admin' ?
        <p className='qst'>Doctor login ? <span onClick={()=>setState('Doctor')}>Click here</span></p>:
        <p className='qst'>Admin login ? <span onClick={()=>setState('Admin')}>Click here</span></p>
        }

      </form>
    </div>
  )
}

export default Login
