import React, { useState } from 'react';
import './Login.css';

const Login = () => {
  const[state, setState] = useState('Admin');
  return (
    <div className='login'>
      <p className='title text-center fw-bold'><span> {state} </span> Login </p>
      <form>
        <div className='mb-3'>
          <p className='mb-1'>Email</p>
          <input className='w-100 px-2 py-1' name='email' type='email' required/>
        </div>
        <div className='mb-3'>
          <p className='mb-1'>Password</p>
          <input className='w-100 px-2 py-1' name='password' type='password' required/>
        </div>
        <button type='submit' className='mb-3 w-100 px-2 py-1'>Login</button>
        {state === 'Admin' ?
        <p className='qst'>Doctor login ? <span onClick={()=>setState('Doctor')}>Click here</span></p>:
        <p className='qst'>Admin login ? <span onClick={()=>setState('Admin')}>Click here</span></p>
        }

      </form>
    </div>
  )
}

export default Login
